import React, { useState } from 'react';
import { Button } from '@mui/material';

const Taskk = () => {
  const [text, setText] = useState('');
  const [todos, setTodos] = useState([
    { id: 1, msg: "Task 1 for todo" },
    { id: 2, msg: "Task 2 for todo" }
  ]);
  const [update , setUpdate] = useState(''); 

  const handleSavetodo = () => {
    if (text.trim() !== '') {
      const newTodo = { id: todos.length + 1, msg: text };
      setTodos([...todos, newTodo]);
      setText('');
    }
  };

  const handleDelete = (id) => {
    setTodos(todos.filter(todo => todo.id !== id));
  };

  const handleUpdate = (id) => {
    const updatetodo = todos.map(todo => 
       todo.id === id ? {...todo , msg : update} : todo 
    )
       setUpdate(updatetodo)
       update(' ')
  } 

  return (
    <div >
      <ul>
        {todos.map(todo => (
          <div key={todo.id} style={{display : 'flex'} }>
            <li>{todo.msg}</li>
            <input 
              type="text" 
              value={todo.id === todo.length+1 ? text : update}
              onChange={(e)=>{
                if(todo.id==todo.length+1){
                    setText(e.target.value) // this is for delete
                }
                else{
                    update(e.target.value)
                }
              }}
             />
            <Button variant='contained' onClick={()=>handleUpdate(todo.id)}>Update</Button>
            <Button variant='contained' onClick={() => handleDelete(todo.id)}>Delete</Button>

          </div>
        ))}
      </ul>     

      <form onSubmit={(e) => { 
        e.preventDefault(); 
        handleSavetodo();
         }}
         >
        <div style={{display : 'flex'}}>
        <input type="text" value={text} onChange={(e) => setText(e.target.value)} />
        <Button variant='contained' type="submit">ADD ME</Button>
        </div>
      </form>
    </div>
  );
};

export default Taskk;
